    while: parseWhile,
    if: parseIf,
    elseif: parseElseIf,
    else: parseElse,
    end: parseEnd,

    act: parseAct,
    log: parseLog,
    dump: parseDump,
    say: parseSay,
    ask: parseAsk,
    option: parseOption,
    swap: parseSwap,
    call: parseCall,
    emit: parseEmit,
    tell: parseTell,
    frame: parseFrame,
    shake: parseShake,
    wait: parseWait,
    play: parsePlay,

    window: parseWindow,
    label: parseLabel,
    fill: parseFill,
    menu: parseMenu,

    ignore: parseIgnore,
    listen: parseListen,
    fin: parseFin,
    invert: parseInvert,
    hide: parseHide,
    draw: parseDraw,
    goto: parseGoto,
    mimic: parseMimic,

    loop: parseLoop,
    once: parseOnce,
    stop: parseStop,
    bpm:parseBpm,
    sound: parseSound,

    store:parseStore,
    restore:parseRestore,
    toss:parseToss,

    crop:parseCrop,

    done:parseDone,
